import React, { useEffect, useRef } from 'react';
import { Timeline, DataSet } from 'vis-timeline/standalone';
import 'vis-timeline/styles/vis-timeline-graph2d.min.css';

const EventTimeline = ({ events }) => {
    const timelineRef = useRef(null);
    const containerRef = useRef(null);

    useEffect(() => {
        if (containerRef.current) {
            // Define the DataSet for items and groups
            const items = new DataSet();
            const groups = new DataSet();

            // Populate items and groups from events
            events.forEach(event => {
                groups.add({
                    id: event.id,
                    content: '', // Leave group content empty since we don't want to display titles here
                });
                items.add({
                    id: event.id,
                    group: event.id,
                    start: event.startDate,
                    end: event.endDate,
                    content: event.title,
                    type: 'range',
                    className: `timeline-event-${event.type.replace(/\s+/g, '-').toLowerCase()}`
                });
            });

            // Define options for the timeline
            const options = {
                stack: false,
                horizontalScroll: true,
                zoomKey: 'ctrlKey',
                maxHeight: '400px',
                orientation: { axis: 'top' },
            };

            // Create the timeline
            timelineRef.current = new Timeline(containerRef.current, items, options);
            timelineRef.current.setGroups(groups);
            timelineRef.current.fit(); // Fit the timeline to show all items
        }

        // Cleanup function
        return () => {
            if (timelineRef.current) {
                timelineRef.current.destroy(); // Destroy the timeline instance
                timelineRef.current = null;
            }
        };
    }, [events]); // Dependency array includes events, so this effect runs when events change

    return <div ref={containerRef} style={{ height: '400px', width: '100%' }} />;
};

export default EventTimeline;
