import React, { useState, useEffect } from 'react';

const EventForm = ({ onSaveEvent, editEventData }) => {
    const [title, setTitle] = useState('');
    const [type, setType] = useState('');
    const [startDate, setStartDate] = useState('');
    const [endDate, setEndDate] = useState('');
    const [isEditing, setIsEditing] = useState(false);

    // Effect to pre-populate form when editing an event
    useEffect(() => {
        if (editEventData) {
            setTitle(editEventData.title);
            setType(editEventData.type);
            setStartDate(editEventData.startDate.split('T')[0]); // Assuming the date is in ISO format
            setEndDate(editEventData.endDate.split('T')[0]);
            setIsEditing(true);
        }
    }, [editEventData]);

    const handleSubmit = (e) => {
        e.preventDefault();
        const eventData = { title, type, startDate, endDate };

        onSaveEvent(eventData, isEditing, editEventData ? editEventData.id : null);

        // Reset form
        resetForm();
    };

    const resetForm = () => {
        setTitle('');
        setType('');
        setStartDate('');
        setEndDate('');
        setIsEditing(false);
    };

    const handleCancel = () => {
        // Additional logic to ensure we exit edit mode
        setEditingEvent(null);
        resetForm();
    };

    return (
        <form onSubmit={handleSubmit}>
            <input
                type="text"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                placeholder="Title"
                required
            />
            <select value={type} onChange={(e) => setType(e.target.value)} required>
                <option value="">Select Type</option>
                <option value="Merger">Merger</option>
                <option value="Dividends">Dividends</option>
                <option value="New Capital">New Capital</option>
                <option value="Hire">Hire</option>
            </select>
            <input
                type="date"
                value={startDate}
                onChange={(e) => setStartDate(e.target.value)}
                required
            />
            <input
                type="date"
                value={endDate}
                onChange={(e) => setEndDate(e.target.value)}
                required
            />
            <button type="submit">{isEditing ? 'Update Event' : 'Save Event'}</button>
            {isEditing && <button type="button" onClick={resetForm}>Cancel</button>}
        </form>
    );
};

export default EventForm;
