import React from 'react';
import Timeline from 'react-calendar-timeline';
import 'react-calendar-timeline/lib/Timeline.css';
import moment from 'moment';

const EventTimeline = ({ events }) => {
    const timelineEvents = events.map(event => ({
        id: event.id,
        title: event.title,
        start_time: moment(event.startDate),
        end_time: moment(event.endDate),
        itemProps: {
            // Add style property to change the background color of the timeline items
            style: {
                background: 'rgba(33, 150, 243, 0.5)', // Or any other color you prefer
                color: 'black', // Text color
            },
        },
    }));

    // Create a group for each event
    const groups = events.map(event => ({
        id: event.id,
        title: event.title,
    }));

    // Make sure that the timeline shows days as the smallest unit
    const defaultTimeStart = moment().startOf('day');
    const defaultTimeEnd = moment().startOf('day').add(1, 'day');

    return (
        <Timeline
            groups={groups}
            items={timelineEvents}
            defaultTimeStart={defaultTimeStart}
            defaultTimeEnd={defaultTimeEnd}
            unit="day" // Set the smallest unit as day
        />
    );
};

export default EventTimeline;
