import './App.css';
import React, { useState } from 'react';
import EventForm from './components/EventForm';
import EventList from './components/EventList';
import EventTimeline from './components/EventTimeline';

const App = () => {
    const [events, setEvents] = useState([]);
    const [editingEvent, setEditingEvent] = useState(null); // State to keep track of the event being edited

    const saveEvent = (eventData, id) => {
        if (id) {
            setEvents(events.map(event => event.id === id ? { ...eventData, id } : event));
        } else {
            const newEvent = { ...eventData, id: Date.now() };
            setEvents([...events, newEvent]);
        }
        setEditingEvent(null); // Always reset editing event after saving or updating
    };

    // Handler to start editing an event
    function startEdit(eventId) {
        setEditingEvent(eventId);
    }

    // Handler to save the edited event
    function saveEdit(eventId, updatedEventData) {
        setEvents(events.map(event => {
            return event.id === eventId ? { ...event, ...updatedEventData } : event;
        }));
        setEditingEvent(null); // Exit editing mode
    }

    const cancelEdit = () => {
        setEditingEvent(null);
    };

    const deleteEvent = (id) => {
        setEvents(events.filter(event => event.id !== id));
    };

    return (
        <div className="App">
            <EventForm onSaveEvent={saveEvent} onCancelEdit={cancelEdit} editEventData={editingEvent}/>
            <EventList events={events} onEdit={editEvent} onDelete={deleteEvent}/>
            <EventTimeline events={events}/>
        </div>
    );
};

export default App;
