import React, { useEffect, useRef } from 'react';
import { Timeline } from 'vis-timeline/standalone';
import 'vis-timeline/styles/vis-timeline-graph2d.min.css';

const EventTimeline = ({ events }) => {
    const timelineRef = useRef();
    const containerRef = useRef(null);

    useEffect(() => {
        if (!timelineRef.current && containerRef.current) {
            const items = events.map(event => ({
                id: event.id,
                content: event.title,
                start: event.startDate,
                end: event.endDate,
                type: 'range',
                className: `timeline-event-${event.type.replace(/\s+/g, '-').toLowerCase()}`, // CSS class based on event type
            }));

            const options = {
                stack: true,
                showCurrentTime: true,
                horizontalScroll: true,
                zoomKey: 'ctrlKey',
                maxHeight: '400px',
                // Modify these options as needed
            };

            timelineRef.current = new Timeline(containerRef.current, items, options);
        }

        return () => {
            if (timelineRef.current) {
                timelineRef.current.destroy();
                timelineRef.current = null;
            }
        };
    }, [events]);

    return <div ref={containerRef} style={{ height: '400px', width: '100%' }} />;
};

export default EventTimeline;
