import React, { useEffect, useRef } from 'react';
import { Timeline, DataSet } from 'vis-timeline/standalone';
import 'vis-timeline/styles/vis-timeline-graph2d.min.css';

const EventTimeline = ({ events }) => {
    const timelineRef = useRef();
    const containerRef = useRef(null);

    useEffect(() => {
        if (containerRef.current) {
            // Create groups and items DataSets
            const groups = new DataSet();
            const items = new DataSet();

            events.forEach((event, index) => {
                // Add a group for each event
                groups.add({
                    id: index,
                    content: event.title, // Group label will be the event title
                });

                // Add an item for each event
                items.add({
                    id: event.id,
                    group: index, // Assign to group by index
                    start: new Date(event.startDate), // Ensure proper Date conversion
                    end: new Date(event.endDate),
                    type: 'range',
                    className: `type-${event.type.replace(/\s/g, '-').toLowerCase()}`, // Class for coloring
                });
            });

            // Timeline options
            const options = {
                stack: false,
                horizontalScroll: true,
                zoomKey: 'ctrlKey',
                maxHeight: '400px',
                orientation: { axis: 'top', item: 'top' },
                // Configure other options as needed
            };

            // Initialize the timeline
            timelineRef.current = new Timeline(containerRef.current, items, options);
            timelineRef.current.setGroups(groups);
            timelineRef.current.fit();
        }

        // Cleanup function
        return () => {
            if (timelineRef.current) {
                timelineRef.current.destroy();
                timelineRef.current = null;
            }
        };
    }, [events]);

    return <div ref={containerRef} style={{ height: '400px', width: '100%' }} />;
};

export default EventTimeline;
