import React, { useEffect, useRef } from 'react';
import { Timeline, DataSet } from 'vis-timeline/standalone';
import 'vis-timeline/styles/vis-timeline-graph2d.min.css';

const EventTimeline = ({ events }) => {
    const timelineRef = useRef();
    const containerRef = useRef(null);

    useEffect(() => {
        if (containerRef.current && !timelineRef.current) {
            // Here we create a DataSet with the vis-timeline package
            const items = new DataSet(
                events.map(event => ({
                    id: event.id,
                    content: event.title,
                    start: event.startDate,
                    end: event.endDate,
                    type: 'range',
                    // You can add styles or classes here if needed
                }))
            );

            const options = {
                // ...options as before
            };

            // Instantiate the timeline
            timelineRef.current = new Timeline(containerRef.current, items, options);
            // Fit the items
            timelineRef.current.fit();
        }

        // Cleanup function to destroy the timeline when the component unmounts
        return () => {
            if (timelineRef.current) {
                timelineRef.current.destroy();
            }
        };
    }, [events]); // Effect dependencies

    return <div ref={containerRef} style={{ height: '400px', width: '100%' }} />;
};

export default EventTimeline;
