export function newCoords(): CoordsSetMember {
  return {
    page: { x: 0, y: 0 },
    client: { x: 0, y: 0 },
    timeStamp: 0,
  }
}

export function coordsToEvent(coords: MockCoords) {
  const event = {
    coords,
    get page() {
      return this.coords.page
