import React, { useEffect, useRef } from 'react';
import { Timeline, DataSet } from 'vis-timeline/standalone';
import 'vis-timeline/styles/vis-timeline-graph2d.min.css';

const EventTimeline = ({ events }) => {
    const timelineRef = useRef();
    const containerRef = useRef(null);

    useEffect(() => {
        if (containerRef.current && !timelineRef.current) {
            const groups = new DataSet();
            const items = new DataSet();

            // Create groups and items
            events.forEach(event => {
                groups.add({
                    id: event.id,
                    content: event.title, // This is the separate column for titles
                });

                items.add({
                    id: event.id,
                    group: event.id, // Assign each item to a group by its own id
                    start: event.startDate,
                    end: event.endDate,
                    type: 'range',
                    className: `timeline-event-${event.type.replace(/\s+/g, '-').toLowerCase()}`, // Assign CSS class based on event type
                });
            });

            const options = {
                stack: false,
                horizontalScroll: true,
                zoomKey: 'ctrlKey',
                maxHeight: '400px',
                orientation: 'top',
            };

            // Initialize the timeline with groups and items
            timelineRef.current = new Timeline(containerRef.current);
            timelineRef.current.setOptions(options);
            timelineRef.current.setGroups(groups);
            timelineRef.current.setItems(items);
        }

        return () => {
            if (timelineRef.current) {
                timelineRef.current.destroy();
            }
        };
    }, [events]);

    return <div ref={containerRef} style={{ height: '400px', width: '100%' }} />;
};

export default EventTimeline;
